import React, { useCallback, useEffect, useState } from 'react';
import Colors from '../../constants/colors';
import ShowcaseExplorer from '../applications/ShowcaseExplorer';
import ShutdownSequence from './ShutdownSequence';
import Toolbar from './Toolbar';
import DesktopShortcut, { DesktopShortcutProps } from './DesktopShortcut';
import { IconName } from '../../assets/icons';

export interface DesktopShortcutProps {
    icon: IconName;
    shortcutName: string;
    onOpen: () => void;
    selected?: boolean; // ← добавь это
}


type ExtendedWindowAppProps<T> = T & WindowAppProps;

const APPLICATIONS: {
    [key in string]: {
        key: string;
        name: string;
        shortcutIcon: IconName;
        component: React.FC<ExtendedWindowAppProps<any>>;
    };
} = {
    showcase: {
        key: 'showcase',
        name: 'My Showcase',
        shortcutIcon: 'showcaseIcon',
        component: ShowcaseExplorer,
    },
};

const Desktop: React.FC<DesktopProps> = () => {
    const [windows, setWindows] = useState<DesktopWindows>({});
    const [shortcuts, setShortcuts] = useState<DesktopShortcutProps[]>([]);
    const [shutdown, setShutdown] = useState(false);
    const [numShutdowns, setNumShutdowns] = useState(1);

    const [selection, setSelection] = useState({
        isSelecting: false,
        startX: 0,
        startY: 0,
        width: 0,
        height: 0,
        originX: 0,
        originY: 0,
    });

    useEffect(() => {
        if (shutdown) rebootDesktop();
    }, [shutdown]);

    useEffect(() => {
        const newShortcuts: DesktopShortcutProps[] = [];
        Object.keys(APPLICATIONS).forEach((key) => {
            const app = APPLICATIONS[key];
            newShortcuts.push({
                shortcutName: app.name,
                icon: app.shortcutIcon,
                onOpen: () => {
                    addWindow(
                        app.key,
                        <app.component
                            onInteract={() => onWindowInteract(app.key)}
                            onMinimize={() => minimizeWindow(app.key)}
                            onClose={() => removeWindow(app.key)}
                            key={app.key}
                        />
                    );
                },
                selected: false, // ← добавлено
            });
        });

        newShortcuts.forEach((shortcut) => {
            if (shortcut.shortcutName === 'My Showcase') {
                shortcut.onOpen();
            }
        });

        setShortcuts(newShortcuts);
    }, []);

    const rebootDesktop = useCallback(() => {
        setWindows({});
    }, []);

    const removeWindow = useCallback((key: string) => {
        setTimeout(() => {
            setWindows((prevWindows) => {
                const newWindows = { ...prevWindows };
                delete newWindows[key];
                return newWindows;
            });
        }, 100);
    }, []);

    const minimizeWindow = useCallback((key: string) => {
        setWindows((prevWindows) => {
            const newWindows = { ...prevWindows };
            newWindows[key].minimized = true;
            return newWindows;
        });
    }, []);

    const getHighestZIndex = useCallback((): number => {
        let highestZIndex = 0;
        Object.keys(windows).forEach((key) => {
            const window = windows[key];
            if (window && window.zIndex > highestZIndex) highestZIndex = window.zIndex;
        });
        return highestZIndex;
    }, [windows]);

    const toggleMinimize = useCallback(
        (key: string) => {
            const newWindows = { ...windows };
            const highestIndex = getHighestZIndex();
            if (newWindows[key].minimized || newWindows[key].zIndex === highestIndex) {
                newWindows[key].minimized = !newWindows[key].minimized;
            }
            newWindows[key].zIndex = highestIndex + 1;
            setWindows(newWindows);
        },
        [windows, getHighestZIndex]
    );

    const onWindowInteract = useCallback(
        (key: string) => {
            setWindows((prevWindows) => ({
                ...prevWindows,
                [key]: {
                    ...prevWindows[key],
                    zIndex: 1 + getHighestZIndex(),
                },
            }));
        },
        [getHighestZIndex]
    );

    const startShutdown = useCallback(() => {
        setTimeout(() => {
            setShutdown(true);
            setNumShutdowns((prev) => prev + 1);
        }, 600);
    }, []);

    const addWindow = useCallback(
        (key: string, element: JSX.Element) => {
            setWindows((prevState) => ({
                ...prevState,
                [key]: {
                    zIndex: getHighestZIndex() + 1,
                    minimized: false,
                    component: element,
                    name: APPLICATIONS[key].name,
                    icon: APPLICATIONS[key].shortcutIcon,
                },
            }));
        },
        [getHighestZIndex]
    );

    const handleMouseDown = (e: React.MouseEvent) => {
        const originX = e.clientX;
        const originY = e.clientY;

        setSelection({
            isSelecting: true,
            originX,
            originY,
            startX: originX,
            startY: originY,
            width: 0,
            height: 0,
        });
    };

    const handleMouseMove = (e: React.MouseEvent) => {
        if (!selection.isSelecting) return;

        const mouseX = e.clientX;
        const mouseY = e.clientY;

        const startX = Math.min(selection.originX, mouseX);
        const startY = Math.min(selection.originY, mouseY);
        const width = Math.abs(mouseX - selection.originX);
        const height = Math.abs(mouseY - selection.originY);

        setSelection((prev) => ({
            ...prev,
            startX,
            startY,
            width,
            height,
        }));
    };

    const handleMouseUp = () => {
        if (selection.isSelecting) {
            setSelection((prev) => ({
                ...prev,
                isSelecting: false,
            }));

            // Обновить выделение ярлыков
            setShortcuts((prevShortcuts) =>
                prevShortcuts.map((shortcut, index) => {
                    const iconX = 6;
                    const iconY = 16 + index * 104;
                    const iconWidth = 64;
                    const iconHeight = 96;

                    const intersects =
                        selection.startX < iconX + iconWidth &&
                        selection.startX + selection.width > iconX &&
                        selection.startY < iconY + iconHeight &&
                        selection.startY + selection.height > iconY;

                    return {
                        ...shortcut,
                        selected: intersects,
                    };
                })
            );
        }
    };

    return !shutdown ? (
        <div
            style={styles.desktop}
            onMouseDown={handleMouseDown}
            onMouseMove={handleMouseMove}
            onMouseUp={handleMouseUp}
        >
            {/* Render windows */}
            {Object.keys(windows).map((key) => {
                const element = windows[key].component;
                if (!element) return <div key={`win-${key}`}></div>;
                return (
                    <div
                        key={`win-${key}`}
                        style={{
                            zIndex: windows[key].zIndex,
                            ...(windows[key].minimized && styles.minimized),
                        }}
                    >
                        {React.cloneElement(element, {
                            key,
                            onInteract: () => onWindowInteract(key),
                            onClose: () => removeWindow(key),
                        })}
                    </div>
                );
            })}

            {/* Render shortcuts */}
            <div style={styles.shortcuts}>
                {shortcuts.map((shortcut, i) => (
                    <div style={{ ...styles.shortcutContainer, top: i * 104 }} key={shortcut.shortcutName}>
                        <DesktopShortcut
                            icon={shortcut.icon}
                            shortcutName={shortcut.shortcutName}
                            onOpen={shortcut.onOpen}
                            selected={shortcut.selected} // ← добавлено
                        />
                    </div>
                ))}
            </div>

            {/* Render selection box */}
            {selection.isSelecting && (
                <div
                    style={{
                        position: 'absolute',
                        left: selection.startX,
                        top: selection.startY,
                        width: selection.width,
                        height: selection.height,
                        backgroundColor: 'rgba(0, 0, 255, 0.3)',
                        border: '2px solid blue',
                        pointerEvents: 'none',
                    }}
                />
            )}
            <Toolbar windows={windows} toggleMinimize={toggleMinimize} shutdown={startShutdown} />
        </div>
    ) : (
        <ShutdownSequence setShutdown={setShutdown} numShutdowns={numShutdowns} />
    );
};

const styles: StyleSheetCSS = {
    desktop: {
        minHeight: '100%',
        flex: 1,
        backgroundColor: Colors.turquoise,
        position: 'relative',
    },
    shutdown: {
        minHeight: '100%',
        flex: 1,
        backgroundColor: '#1d2e2f',
    },
    shortcutContainer: {
        position: 'absolute',
    },
    shortcuts: {
        position: 'absolute',
        top: 16,
        left: 6,
    },
    minimized: {
        pointerEvents: 'none',
        opacity: 0,
    },
};

export default Desktop;
