import React, { useState } from 'react';
import { Link } from '../general';
import { useNavigate } from 'react-router-dom';

import portfolioIcon from '../../assets/icons/portfolio.ico';
import experienceIcon from '../../assets/icons/experience.ico';
import aboutIcon from '../../assets/icons/about.ico';
import contactIcon from '../../assets/icons/contact.ico';

import forhire from '../../assets/pictures/forHireGif.gif';

export interface HomeProps {}

const Home: React.FC<HomeProps> = (props) => {
    const navigate = useNavigate();

    const goToContact = () => {
        navigate('/contact');
    };

    return (
        <div style={styles.page}>
            <div style={styles.header}>
                <h1 style={styles.name}>Tymoshenko Production</h1>
                <h2>Motion Designer and Web Developer</h2>
            </div>

            <div style={styles.buttons}>
                <Link
                    containerStyle={styles.link}
                    to="projects"
                    text={
                        <img
                            src={portfolioIcon}
                            alt="Portfolio"
                            title="Go to Portfolio"
                            style={styles.icon}
                        />
                    }
                />
                <Link
                    containerStyle={styles.link}
                    to="experience"
                    text={
                        <img
                            src={experienceIcon}
                            alt="Experience"
                            title="Go to Experience"
                            style={styles.icon}
                        />
                    }
                />
                <Link
                    containerStyle={styles.link}
                    to="about"
                    text={
                        <img
                            src={aboutIcon}
                            alt="About"
                            title="Go to About"
                            style={styles.icon}
                        />
                    }
                />
                <Link
                    containerStyle={styles.link}
                    to="contact"
                    text={
                        <img
                            src={contactIcon}
                            alt="Contact"
                            title="Go to Contact"
                            style={styles.icon}
                        />
                    }
                />
            </div>

            <div style={styles.forHireContainer} onMouseDown={goToContact}>
                <img src={forhire} alt="For Hire" style={styles.image} />
            </div>
        </div>
    );
};

const styles: { [key: string]: React.CSSProperties } = {
    page: {
        left: 0,
        right: 0,
        top: 0,
        position: 'absolute',
        justifyContent: 'center',
        alignItems: 'center',
        flexDirection: 'column',
        height: '100%',
        display: 'flex',
    },
    header: {
        textAlign: 'center',
        marginBottom: 64,
        marginTop: 64,
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        display: 'flex',
    },
    buttons: {
        display: 'flex',
        justifyContent: 'space-between',
        gap: 32,
    },
    image: {
        width: 800,
    },
    link: {
        padding: 16,
    },
    nowHiring: {
        backgroundColor: 'red',
        padding: 16,
    },
    forHireContainer: {
        marginTop: 64,
        width: '100%',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        cursor: 'pointer',
    },
    name: {
        fontSize: 72,
        marginBottom: 16,
        lineHeight: 0.9,
    },
    icon: {
        width: 48,
        height: 48,
        cursor: 'pointer',
        transition: 'transform 0.2s',
    },
};

export default Home;
