import React from 'react';
import { useNavigate } from 'react-router-dom';
import { FaBriefcase, FaBook, FaUser, FaEnvelope } from 'react-icons/fa';
import { IconType } from 'react-icons';

import forhire from '../../assets/pictures/forHireGif.gif';

export interface HomeProps {}

const Home: React.FC<HomeProps> = () => {
    const navigate = useNavigate();

    const goToContact = () => {
        navigate('/contact');
    };

    const IconLink = ({
                          icon: Icon,
                          label,
                          to,
                      }: {
        icon: React.ComponentType<{ style?: React.CSSProperties }>; // ✅ фикс типов
        label: string;
        to: string;
    }) => (
        <div
            style={styles.iconWrapper}
            onClick={() => navigate(to)}
            onMouseEnter={(e) => {
                const tooltip = e.currentTarget.querySelector('.tooltip') as HTMLElement;
                tooltip.style.visibility = 'visible';
                tooltip.style.opacity = '1';
            }}
            onMouseLeave={(e) => {
                const tooltip = e.currentTarget.querySelector('.tooltip') as HTMLElement;
                tooltip.style.visibility = 'hidden';
                tooltip.style.opacity = '0';
            }}
        >
            <Icon style={styles.iconLink} />
            <div className="tooltip" style={styles.tooltip}>
                {label}
            </div>
        </div>
    );

    return (
        <div style={styles.page}>
            <div style={styles.header}>
                <h1 style={styles.name}>Tymoshenko Production</h1>
                <h2>Motion Designer and Web Developer</h2>
            </div>
            <div style={styles.buttons}>
                <IconLink icon={FaBriefcase} label="Portfolio" to="/projects" />
                <IconLink icon={FaBook} label="Experience" to="/experience" />
                <IconLink icon={FaUser} label="About" to="/about" />
                <IconLink icon={FaEnvelope} label="Contact" to="/contact" />
            </div>
            <div style={styles.forHireContainer} onMouseDown={goToContact}>
                {/* <img src={forhire} alt="For Hire" /> */}
            </div>
        </div>
    );
};

const styles: StyleSheetCSS = {
    page: {
        left: 0,
        right: 0,
        top: 0,
        position: 'absolute',
        justifyContent: 'center',
        alignItems: 'center',
        flexDirection: 'column',
        height: '100%',
    },
    header: {
        textAlign: 'center',
        marginBottom: 64,
        marginTop: 64,
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
    },
    buttons: {
        display: 'flex',
        gap: 32,
        justifyContent: 'center',
        alignItems: 'center',
    },
    iconWrapper: {
        position: 'relative',
        cursor: 'pointer',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
    iconLink: {
        fontSize: 32,
        color: '#000',
    },
    tooltip: {
        visibility: 'hidden',
        backgroundColor: '#000',
        color: '#fff',
        textAlign: 'center',
        borderRadius: 6,
        padding: '6px 12px',
        position: 'absolute',
        zIndex: 1,
        bottom: -35,
        left: '50%',
        transform: 'translateX(-50%)',
        opacity: 0,
        transition: 'opacity 0.3s',
        fontSize: 12,
        whiteSpace: 'nowrap',
    },
    image: {
        width: 800,
    },
    nowHiring: {
        backgroundColor: 'red',
        padding: 16,
    },
    forHireContainer: {
        marginTop: 64,
        width: '100%',
        justifyContent: 'center',
        alignItems: 'center',
        cursor: 'pointer',
    },
    name: {
        fontSize: 72,
        marginBottom: 16,
        lineHeight: 0.9,
    },
};

export default Home;
