import React, { useState, useRef } from 'react';
import DesktopShortcut from './DesktopShortcut'; // Импорт своего компонента
import Toolbar from './Toolbar';
import ShutdownSequence from './ShutdownSequence';
import { Colors } from './constants'; // Импорт цветов, если используешь

interface DesktopShortcutProps {
    icon: string;
    shortcutName: string;
    onOpen: () => void;
    isSelected?: boolean;
}

const Desktop = ({ shortcuts, windows, onWindowInteract, removeWindow, toggleMinimize, startShutdown, shutdown, numShutdowns }) => {
    const [selection, setSelection] = useState({
        isSelecting: false,
        originX: 0,
        originY: 0,
        startX: 0,
        startY: 0,
        width: 0,
        height: 0,
    });

    const [selectedShortcuts, setSelectedShortcuts] = useState<Set<string>>(new Set());
    const containerRef = useRef<HTMLDivElement | null>(null);

    const getSelectionBox = () => {
        const { startX, startY, width, height } = selection;
        return {
            left: Math.min(startX, startX + width),
            top: Math.min(startY, startY + height),
            right: Math.max(startX, startX + width),
            bottom: Math.max(startY, startY + height),
        };
    };

    const handleMouseDown = (e: React.MouseEvent) => {
        const rect = containerRef.current?.getBoundingClientRect();
        const originX = e.clientX - (rect?.left || 0);
        const originY = e.clientY - (rect?.top || 0);

        setSelection({
            isSelecting: true,
            originX,
            originY,
            startX: originX,
            startY: originY,
            width: 0,
            height: 0,
        });
        setSelectedShortcuts(new Set()); // сброс выделения
    };

    const handleMouseMove = (e: React.MouseEvent) => {
        if (!selection.isSelecting) return;

        const rect = containerRef.current?.getBoundingClientRect();
        const mouseX = e.clientX - (rect?.left || 0);
        const mouseY = e.clientY - (rect?.top || 0);

        const width = mouseX - selection.originX;
        const height = mouseY - selection.originY;

        setSelection((prev) => ({
            ...prev,
            startX: selection.originX,
            startY: selection.originY,
            width,
            height,
        }));

        // Вычисление рамки
        const box = {
            left: Math.min(selection.originX, mouseX),
            right: Math.max(selection.originX, mouseX),
            top: Math.min(selection.originY, mouseY),
            bottom: Math.max(selection.originY, mouseY),
        };

        // Проверка попадания иконок
        const newlySelected = new Set<string>();
        shortcuts.forEach((shortcut, i) => {
            const iconX = 6; // `left` из стилей
            const iconY = 16 + i * 104; // `top` из `shortcutContainer`
            const iconW = 64;
            const iconH = 96;

            const intersects =
                box.left < iconX + iconW &&
                box.right > iconX &&
                box.top < iconY + iconH &&
                box.bottom > iconY;

            if (intersects) {
                newlySelected.add(shortcut.shortcutName);
            }
        });

        setSelectedShortcuts(newlySelected);
    };

    const handleMouseUp = () => {
        setSelection((prev) => ({ ...prev, isSelecting: false }));
    };

    return !shutdown ? (
        <div
            ref={containerRef}
            style={styles.desktop}
            onMouseDown={handleMouseDown}
            onMouseMove={handleMouseMove}
            onMouseUp={handleMouseUp}
        >
            {/* Render windows */}
            {Object.keys(windows).map((key) => {
                const element = windows[key].component;
                if (!element) return <div key={`win-${key}`}></div>;
                return (
                    <div
                        key={`win-${key}`}
                        style={{
                            zIndex: windows[key].zIndex,
                            ...(windows[key].minimized && styles.minimized),
                        }}
                    >
                        {React.cloneElement(element, {
                            key,
                            onInteract: () => onWindowInteract(key),
                            onClose: () => removeWindow(key),
                        })}
                    </div>
                );
            })}

            {/* Render shortcuts */}
            <div style={styles.shortcuts}>
                {shortcuts.map((shortcut, i) => (
                    <div
                        style={{ ...styles.shortcutContainer, top: i * 104 }}
                        key={shortcut.shortcutName}
                    >
                        <DesktopShortcut
                            icon={shortcut.icon}
                            shortcutName={shortcut.shortcutName}
                            onOpen={shortcut.onOpen}
                            isSelected={selectedShortcuts.has(shortcut.shortcutName)}
                        />
                    </div>
                ))}
            </div>

            {/* Selection box */}
            {selection.isSelecting && (
                <div
                    style={{
                        position: 'absolute',
                        left: Math.min(selection.startX, selection.startX + selection.width),
                        top: Math.min(selection.startY, selection.startY + selection.height),
                        width: Math.abs(selection.width),
                        height: Math.abs(selection.height),
                        backgroundColor: 'rgba(0, 0, 255, 0.3)',
                        border: '1px solid blue',
                        pointerEvents: 'none',
                    }}
                />
            )}

            <Toolbar
                windows={windows}
                toggleMinimize={toggleMinimize}
                shutdown={startShutdown}
            />
        </div>
    ) : (
        <ShutdownSequence setShutdown={startShutdown} numShutdowns={numShutdowns} />
    );
};

const styles = {
    desktop: {
        minHeight: '100%',
        flex: 1,
        backgroundColor: Colors.turquoise,
        position: 'relative' as const,
    },
    shortcutContainer: {
        position: 'absolute' as const,
        left: 6,
    },
    shortcuts: {
        position: 'absolute' as const,
        top: 16,
        left: 6,
    },
    minimized: {
        pointerEvents: 'none' as const,
        opacity: 0,
    },
};

export default Desktop;
